mapper.select:
SELECT a.*
,(select name from cost_type c where c.code=a.costTypeCode) as costTypeName
FROM cost_record a
WHERE a.deleteFlag = 0

  and a.costDate >= #{costDateStart}


  and a.costDate <= #{costDateEnd}


  and DATE_FORMAT(a.costDate,'%Y-%m') = #{costMonthStr}


  and DATE_FORMAT(a.costDate,'%Y') = #{costYearStr}


  and a.costPrice >= #{costPriceStart}


  and a.costPrice <= #{costPriceEnd}


  and a.costItemId = #{costItemId}


  and a.remark like concat('%',#{remark},'%')


  and a.costTypeCode like concat(#{costTypeCode},'%')

order by a.costDate desc

select costDate,sum(costPrice) costPrice from cost_record a
where deleteflag=0

  and a.costDate >= #{costDateStart}


  and a.costDate <= #{costDateEnd}


  and DATE_FORMAT(a.costDate,'%Y-%m') = #{costMonthStr}


  and DATE_FORMAT(a.costDate,'%Y') = #{costYearStr}

group by costDate
having 1=1

  and sum(costPrice) >= #{costPriceStart}


  and sum(costPrice) <= #{costPriceEnd}


  and group_concat(distinct costItemId) like concat('%'+#{costItemId}+''%')

order by costDate desc
